package com.univ.tours.app.GestionEvent.securingweb;import com.univ.tours.app.GestionEvent.entities.Personne;import org.springframework.security.core.GrantedAuthority;import org.springframework.security.core.userdetails.UserDetails;import java.util.Collection;public class CustomUserDetails implements UserDetails {    private Personne personne;    public CustomUserDetails(Personne personne) {        this.personne = personne;    }    @Override    public Collection<? extends GrantedAuthority> getAuthorities() {        return null;    }    @Override    public String getPassword() {        return personne.getMdp();    }    @Override    public String getUsername() {        return personne.getEmail();    }    @Override    public boolean isAccountNonExpired() {        return true;    }    @Override    public boolean isAccountNonLocked() {        return true;    }    @Override    public boolean isCredentialsNonExpired() {        return true;    }    @Override    public boolean isEnabled() {        return true;    }}