package com.univ.tours.app.GestionEvent.securingweb;import com.univ.tours.app.GestionEvent.dao.PersonneRepository;import com.univ.tours.app.GestionEvent.entities.Personne;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.security.core.userdetails.UserDetails;import org.springframework.security.core.userdetails.UserDetailsService;import org.springframework.security.core.userdetails.UsernameNotFoundException;public class CustomUserDetailsService implements UserDetailsService {    @Autowired    private PersonneRepository personneRepository;    @Override    public UserDetails loadUserByUsername(String email) throws UsernameNotFoundException {        Personne personne = personneRepository.findPersonneByEmail(email);        if (personne == null) {            throw new UsernameNotFoundException("User not found");        }        return new CustomUserDetails(personne);    }}